import React, { useEffect } from 'react';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import 'leaflet.heat/dist/leaflet-heat.js'; // Import the Leaflet.heat plugin
import axios from 'axios';

function App() {
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/get-map');
        const geojsonData = response.data;

        const map = L.map('map').setView([-33.02, -71.55], 12);

        L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
          attribution: 'Map data © <a href="https://openstreetmap.org">OpenStreetMap</a> contributors',
        }).addTo(map);

        const coordinates = geojsonData.features[0].geometry.coordinates[0];
        const latLngs = coordinates.map(coords => L.latLng(coords[1], coords[0]));

        const heatLayer = L.heatLayer(latLngs).addTo(map);

        // Optional: You can customize the heatmap appearance using the options below
        // const heatOptions = {
        //   radius: 25,
        //   blur: 15,
        //   maxZoom: 18,
        // };
        // const heatLayer = L.heatLayer(latLngs, heatOptions).addTo(map);

      } catch (error) {
        console.error('Error fetching GeoJSON data:', error);
      }
    };

    fetchData();
  }, []);

  return <div id="map" style={{ height: '100vh' }} />;
}

export default App;
