import React, { useEffect, useState } from 'react';
import axios from 'axios';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

const MapComponent = () => {
  const [locations, setLocations] = useState([]);
  const [points, setPoints] = useState([]);
  const mapRef = React.createRef();
  let map = null; // Keep track of the map instance

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/get-map');

        // Separate the responses for locations and points
        const [locationsResponse, pointsResponse] = response;

        setLocations(locationsResponse.data.features);
        setPoints(pointsResponse.data.features);
      } catch (error) {
        console.log(error);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (!mapRef.current) {
      // Map container is not yet rendered, do nothing
      return;
    }

    // Remove the previous map instance if it exists
    if (map) {
      map.remove();
    }

    // Initialize the map
    map = L.map(mapRef.current).setView([0, 0], 2);

    // Add a tile layer to the map
    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: 'Map data &copy; <a href="https://www.openstreetmap.org/">OpenStreetMap</a> contributors',
      maxZoom: 18
    }).addTo(map);

    // Add the locations to the map as markers
    locations.forEach((feature) => {
      const coordinates = feature.geometry.coordinates;
      const marker = L.marker(coordinates.reverse()).addTo(map);
    });

    // Add the points to the map as markers
    points.forEach((feature) => {
      const coordinates = feature.geometry.coordinates;
      const marker = L.marker(coordinates.reverse()).addTo(map);
    });
  }, [locations, points]);

  return <div ref={mapRef} style={{ height: '400px', width: '100%' }}></div>;
};

export default MapComponent;
