import React, { useEffect, useRef } from 'react';
import L from 'leaflet';

const MapComponent = () => {
  const mapRef = useRef(null); // Use useRef to preserve the map variable's value

  useEffect(() => {
    mapRef.current = L.map('mapid').setView([51.505, -0.09], 13); // Assign value to mapRef.current instead of map

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: 'Map data &copy; OpenStreetMap contributors',
    }).addTo(mapRef.current);

    return () => {
      mapRef.current.remove(); // Clean up the map when the component unmounts
    };
  }, []); // Empty dependency array to run the effect only once

  return <div id="mapid" style={{ height: '400px' }}></div>;
};

export default MapComponent;
