import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import L from 'leaflet';

const MapComponent = () => {
  const [locations, setLocations] = useState([]);
  const [points, setPoints] = useState([]);
  const mapRef = useRef(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/get-map');

        // Separate the responses for locations and points
        const [locationsResponse, pointsResponse] = response;

        setLocations(locationsResponse.data.features);
        setPoints(pointsResponse.data.features);
      } catch (error) {
        console.log(error);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (mapRef.current) {
      // Map is already initialized, do nothing
      return;
    }

    // Initialize the map
    const map = L.map('map').setView([0, 0], 2);
    mapRef.current = map;

    // Add a tile layer to the map
    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: 'Map data &copy; <a href="https://www.openstreetmap.org/">OpenStreetMap</a> contributors',
      maxZoom: 18
    }).addTo(map);
  }, []);

  useEffect(() => {
    if (!mapRef.current) {
      // Map is not yet initialized, do nothing
      return;
    }

    // Clear existing markers
    mapRef.current.eachLayer((layer) => {
      if (layer instanceof L.Marker) {
        mapRef.current.removeLayer(layer);
      }
    });

    // Add the locations to the map as markers
    locations.forEach((feature) => {
      const coordinates = feature.geometry.coordinates;
      const marker = L.marker(coordinates.reverse()).addTo(mapRef.current);
    });

    // Add the points to the map as markers
    points.forEach((feature) => {
      const coordinates = feature.geometry.coordinates;
      const marker = L.marker(coordinates.reverse()).addTo(mapRef.current);
    });
  }, [locations, points]);

  return <div id="map" style={{ height: '400px', width: '100%' }}></div>;
};

export default MapComponent;
